angular.module('Aggie')
  .controller('LoginController', ['$scope', '$rootScope', 'AuthService', '$location', function($scope, $rootScope, AuthService, $location) {
    $scope.error = {};
    $scope.user = {};

    $scope.login = function(form) {
      AuthService.login({
          'username': $scope.user.username,
          'password': $scope.user.password
        },
        function(err) {
          if (!err) {
            $rootScope.$broadcast('auth-login-success');
            $location.path('/');
          } else {
            $rootScope.$broadcast('auth-login-failed');
            console.log('Failed');
          }
      });
    };
  }]);

angular.module('Aggie')
  .controller('PasswordResetController', [
    '$scope',
    '$location',
    '$http',
    'Session', // temporary
    'AuthService',
    function($scope, $location, $http, Session, AuthService) {
      $scope.alerts = [];

      var passwordsMatch = function() {
        return $scope.user.password ==
          $scope.user.password_confirmation;
      };

      $scope.resetPassword = function() {
        if (passwordsMatch()) {
          Session.username = 'admin'; // temporary
          var username = AuthService.currentUser();
          var params = {  password: $scope.user.password }
          $http.put('/api/v1/user/' + username, params)
            .success(function(user) {
              $location.path('/');
            }).error(function(message, respStatus) {
              $scope.alerts.push(message);
            });
        } else {
          $scope.alerts.push('Passwords do not match');
        }
      };
    }
  ]);

angular.module('Aggie')
  .factory('AuthService', [
    '$http',
    'Session',
    function AuthService($http, Session) {
      return {
        login: function(credentials, callback) {
          $http.post('/login', credentials)
            .then(function (res) {
              Session.create(res.data._id, res.data.username);
              return callback();
            }, function(err) {
              return callback(err);
            });
        },

        isAuthenticated: function() {
          return !!Session.id;
        },

        currentUser: function() {
          return Session.username;
        }
      }
    }
  ]);

angular.module('Aggie')
  .service('Session', function() {
    this.create = function (id, username) {
      this.id = id;
      this.username = username;
    };

    this.destroy = function() {
      this.id = null;
      this.username = null;
    };

    return this;
  });

